import { Component, signal } from "@angular/core";
import { ThemeService } from "../../services/theme.service";

@Component({
  selector: "app-navbar",
  templateUrl: "./navbar.component.html",
  styleUrls: ["./navbar.component.scss"],
})
export class NavbarComponent {
  public theme: signal<boolean>;

  constructor(private readonly themeService: ThemeService) {
    this.theme = this.themeService.theme;
  }

  public changeTheme(): void {
    this.themeService.themeChange();
  }

  public accessMenu(): void {
    console.log("Working");
  }
}
<nav class="nav">
  <span class="nav-logo"><a routerLink="">Logo</a></span>
  <ul class="nav-links">
    <li class="nav-links-item">
      <a class="nav-links-link" routerLink="shopping">Shopping</a>
      <a class="nav-links-link" routerLink="shopping">Shopping</a>
      <a class="nav-links-link" routerLink="shopping">Shopping</a>
    </li>
  </ul>
  <div class="nav-clickables">
    <button *ngIf="theme()" (click)="changeTheme()">
      <i class="pi pi-sun"></i>
    </button>
    <button *ngIf="!theme()" (click)="changeTheme()">
      <i class="pi pi-moon"></i>
    </button>
    <button (click)="accessMenu()"><i class="pi pi-bars"></i></button>
    <button (click)="accessMenu()"><i class="pi pi-times"></i></button>
  </div>
</nav>
